mapper.update:
UPDATE TB_USER
SET profile_picture = #{filePath}
WHERE id = #{userID}

UPDATE TB_USER
SET fail_count = fail_count + 1
WHERE id = #{username};

UPDATE TB_USER
SET fail_count = 0
WHERE id = #{username};

UPDATE TB_USER 
SET rank = #{newRank}, is_admin = #{newRole}
WHERE user_num = #{user_num}
